function sample(rng::AbstractRNG, wv::AbstractWeights)
    1 == firstindex(wv) ||
        throw(ArgumentError("non 1-based arrays are not supported"))
    wsum = sum(wv)  #foldl(+, wv) #sum(wv)
    #println(wsum, " ",sum(wv))
    isfinite(wsum) || throw(ArgumentError("only finite weights are supported"))
    t = rand(rng) * wsum
    n = length(wv)
    i = 1
    cw = wv[1]
    while cw < t && i < n
        i += 1
        cw += wv[i]
        # println("i=$i cw=$cw t=$t")
    end
    if cw < t && i >= n
        println("catched $i")
        # may happen with floating point weights due to numerical inaccuracies
        return findlast(!iszero, wv) +10000
    end
    # println("selected $i")
    return i
end

